package scaler.module1.arrays.carryforward;

public class AQ1SpecialSubsequencesAG {
  //  Problem Description
  //  You have given a string A having Uppercase English letters.
  //  You have to find how many times subsequence "AG" is there in the given string.
  //  NOTE: Return the answer modulo 109 + 7 as the answer can be very large.
  //      Problem Constraints
  // 1 <= length(A) <= 105
  //  Input Format
  //  First and only argument is a string A.
  //  Output Format
  //  Return an integer denoting the answer.
  //  Example Input
  //  Input 1:
  //  A = "ABCGAG"
  //  Input 2:
  //  A = "GAB"
  //  Example Output
  //  Output 1:
  //      3
  //  Output 2:
  //      0
  //  Example Explanation
  //  Explanation 1:
  //  Subsequence "AG" is 3 times in given string
  //  Explanation 2:
  //  There is no subsequence "AG" in the given string.
  public int solve(String A) {
    char[] chars = A.toCharArray();
    int countOfG = 0;
    long ans = 0;
    for (int i = chars.length - 1; i >= 0; i--) {
      if (chars[i] == 'G') {
        countOfG++;
      }
      if (chars[i] == 'A') {
        ans = ans + countOfG;
      }
    }
    long mod = (long) (Math.pow(10, 9) + 7);
    return (int) (ans % mod);
  }

  public static void main(String[] args) {
    String A = "ABCGAG";
    System.out.println(new AQ1SpecialSubsequencesAG().solve(A));
    String B = "GAB";
    System.out.println(new AQ1SpecialSubsequencesAG().solve(B));
    String C =
        "";
    System.out.println(new AQ1SpecialSubsequencesAG().solve(C));
    //    499999986
  }
}
